{"filter":false,"title":"octopress_filters.rb","tooltip":"/plugins/octopress_filters.rb","undoManager":{"mark":3,"position":3,"stack":[[{"group":"doc","deltas":[{"start":{"row":0,"column":0},"end":{"row":21,"column":3},"action":"insert","lines":["# Filters taken from the Octopress project by Brandon Mathis.","# https://github.com/imathis/octopress/blob/master/plugins/octopress_filters.rb","module Jekyll","","  module Filters","","    # Escapes CDATA sections in post content","    def cdata_escape(input)","      input.gsub(/<!\\[CDATA\\[/, '&lt;![CDATA[').gsub(/\\]\\]>/, ']]&gt;')","    end","","    # Replaces relative urls with full urls","    def expand_urls(input, url='')","      url ||= '/'","      input.gsub /(\\s+(href|src)\\s*=\\s*[\"|']{1})(\\/[^\\\"'>]*)/ do","        $1+url+$3","      end","    end","","  end","","end"]}]}],[{"group":"doc","deltas":[{"start":{"row":21,"column":3},"end":{"row":21,"column":4},"action":"insert","lines":["\\"]}]}],[{"group":"doc","deltas":[{"start":{"row":21,"column":3},"end":{"row":21,"column":4},"action":"remove","lines":["\\"]}]}],[{"group":"doc","deltas":[{"start":{"row":21,"column":3},"end":{"row":22,"column":0},"action":"insert","lines":["",""]}]}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":22,"column":0},"end":{"row":22,"column":0},"isBackwards":false},"options":{"tabSize":2,"useSoftTabs":true,"guessTabSize":false,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1425263339000,"hash":"198bdcc3c44a31911e5ed3dbbaef1c0fcccf0166"}